@font-face {
  font-family: SignikaBold;
  src: url("./fonts/SignikaNegative-Bold.ttf");
}

@font-face {
  font-family: SignikaLight;
  src: url("./fonts/SignikaNegative-Light.ttf");
}

@font-face {
  font-family: Signika;
  src: url("./fonts/SignikaNegative-Regular.ttf");
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  background: #642b73; /* fallback for old browsers */
  background: -webkit-linear-gradient(
    to right,
    #642b73,
    #c6426e
  ); /* Chrome 10-25, Safari 5.1-6 */
  background: linear-gradient(to right, #642b73, #c6426e);
  color: white;
  overflow: hidden;
}

#title {
  margin-top: 60px;
  text-align: center;

  .ignite {
    font-size: 60px;
    font-family: 'SignikaBold';
  }

  .countdown {
    font-size: 20px;
    font-family: 'Signika';
  }
}

.volume {
  width: 165px;
  display: inline-block;
  .slider {
    -webkit-appearance: none;
    width: 100%;
    height: 3px;
    background: #d3d3d3;
    outline: none;
    -webkit-transition: .2s;
    transition: opacity .2s;

    &::-webkit-slider-thumb {
      -webkit-appearance: none;
      appearance: none;
      width: 15px;
      border-radius: 20px;
      height: 15px;
      background: white;
      cursor: pointer;
    }

    &::-moz-range-thumb {
      width: 15px;
      border-radius: 20px;
      height: 15px;
      background: white;
      cursor: pointer;
    }
  }
}

.mainSection {
  width: 100%;
  margin-top: 0px;
  height: 280px;
  display: flex;
  justify-content: center;

  .controls {
    width: 250px;
    height: 25px;  
    padding: 0 10px;
    margin-top: 10px;

    .pause {
      height: 25px;
      width: 25px;
      cursor: pointer;
      display: inline-block;
      font-size: 21px;
      i {
        width: 25px;
        height: 25px;
        top: 5px;
        left: 4px;
        position: relative;
      }
    }
    
    
    .songRequest {
      height: 25px;
      width: 25px;
      cursor: pointer;
      display: inline-block;
      font-size: 21px;
      i {
        width: 25px;
        height: 25px;
        top: 5px;
        left: 4px;
        position: relative;
      }
    }


  }

  .subMain {
    justify-content: center;
    width: 250px;
    height: 250px;
    display: block;
    position: relative;
    background: white;

    .detailsOuter {
      height: 250px;
      display: flex;
      align-items: center;
      position: absolute;
      left: 250px;
      top: 0;
      width: fit-content;
      .details {
        height: 60px;
        margin-left: 10px;
        .artistName {     
          font-size: 30px;
          font-family: 'SignikaBold';
          white-space: nowrap;
        }
        .songName {
          font-size: 18px;
          font-family: 'SignikaLight';
          white-space: nowrap;
        }
      }
    }
    
    .prevdetailsOuter {
      height: 250px;
      display: flex;
      align-items: center;
      position: absolute;
      transform: translateX(-110%);
      top: 0;
      width: fit-content;
      .prevdetails {
        height: 60px;
        margin-left: 10px;
        .dj {     
          font-size: 30px;
          font-family: 'SignikaBold';
          text-align: right;
          white-space: nowrap;
        }
        .listeners {
          font-size: 20px;
          font-family: 'SignikaLight';
          white-space: nowrap;
        }
      }
    }

    .art {
      width: 250px;
      height: 250px;
      box-shadow: 0 1px 5px rgba(0, 0, 0, 0.46);
      user-select: none;
    }

    .imageOverlay {
      background: rgba(0, 0, 0, 0.4);
      transition: opacity 0.3s;
      user-select: none;
      text-align: center;
      line-height: 250px;
      height: 250px;
      width: 250px;
      position: absolute;
      top: 0;
      left: 0;
      display: flex;
      align-items: center;
      justify-content: center;
      cursor: pointer;
    }

  }

}

.social {
  width: 400px;
  display: flex;
  justify-content: center;
  margin: 30px auto 0;
  height: 70px;
  
  .appButton {
    outline: 0;
    text-decoration: none;
    margin: 0 20px;
    height: 35px;
    border-radius: 70px;
    cursor: pointer;
    font-size: 30px;

    .fa-discord {
      color:#7289da
    }
    
    .fa-twitter {
      color: #00acee
    }
  }
}

#arrow,
#arrow::before {
  position: absolute;
  width: 8px;
  height: 8px;
  z-index: -1;
}

#arrow::before {
  content: '';
  transform: rotate(45deg);
  background: white;
}

#modal-1-content {
  align-items: center;
  display: flex;
  flex-direction: column;
  .modalInput {
    width: 300px;
    height: 30px;
    margin: 2px 20px;
    background: #f3f3f3;
    border-radius: 4px;
    border: solid 1px gray;
    outline: 0;
    padding: 10px;
  }

  #songSubmit {
    margin-top: 40px;
    width: 300px;
  }
}
